import React, { Component } from "react";
import { Form, Row, Col, Button } from "react-bootstrap";

class AdminRowForm extends Component {
	render() {
		return (
			<div className = "formContainer">
					<form onSubmit={this.props.handleFormSubmit}>
						<Row>
							<Col>
								<Form.Label>Question</Form.Label>
								<Form.Control required as="textarea" rows="3" name="prompt" value={this.props.prompt} onChange={this.props.handleInputChange} />
							</Col>
							<Col>
								<Form.Label>Type</Form.Label>
								<Form.Control required as="select" name="questionType" value={this.props.questionType} onChange={this.props.handleInputChange}>
									<option disabled>Choose...</option>
									<option value="RR">Radio</option>
									<option value="FR">Free</option>
									<option value="DR">Dropdown</option>
								</Form.Control>
							</Col>
						</Row>
							<Row style={group}>
								<Button variant="red" type="submit">Add</Button>
							</Row>
					</form>
			</div>
		);
	}
}

var group = {
	margin: "2%",
	flexDirection: "column",
	alignItems: "center",
}

export default AdminRowForm;
